import React from 'react'
import logo from './logo.svg'
import candlestick from './candlestick.png'
import './App.css';
import Coin from './components/Coin/Coin'
import AccountBalance from './components/AccountBalance/AccountBalance'
import { v4 as uuidv4 } from 'uuid'
import CoinList from './components/CoinList/CoinList';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      balance: 10000,
      coinData: [
        {
          name: 'Bitcoin',
          ticker: 'BTC',
          price: 35000
        },
        {
          name: 'Ethereum',
          ticker: 'ETH',
          price: 1400
        },
        {
          name: 'Tether',
          ticker: 'USDT',
          price: 1
        },
        {
          name: 'Ripple',
          ticker: 'XRP',
          price: 0.50
        },
        {
          name: 'Polkadot',
          ticker: 'DOT',
          price: 15.95
        },
        {
          name: 'Cardano',
          ticker: 'ADA',
          price: 0.42
        },
        {
          name: 'Chainlink',
          ticker: 'LINK',
          price: 22.38
        },
        {
          name: 'Uniswap',
          ticker: 'UNI',
          price: 19.56
        }
      ]
    }
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
            <img src={candlestick} alt="React logo" className="App-logo" />
            <h1 className="App-title">
              Coin Exchange
            </h1>
        </header>
        <AccountBalance amount={this.state.balance} />
        <CoinList coinData={this.state.coinData} />
      </div>
    );
  }

}

export default App;
