import React from 'react'
import AccountBalance from './components/AccountBalance'
import { v4 as uuidv4 } from 'uuid'
import CoinList from './components/CoinList';
import ExchangeHeader from './components/ExchangeHeader';
import styled from 'styled-components';
import axios from 'axios';

const Div = styled.div`
  text-align: center;
  background-color: rgb(20, 56, 97);
  color: #cccccc;
`;

const COIN_COUNT = 10;

class App extends React.Component {
  state = {
    balance: 10000,
    showBalance: true,
    coinData: [
      // {
      //   name: 'Bitcoin',
      //   ticker: 'BTC',
      //   price: 35000,
      //   balance: 0.5
      // },
      // {
      //   name: 'Ethereum',
      //   ticker: 'ETH',
      //   price: 1400,
      //   balance: 87
      // },
      // {
      //   name: 'Tether',
      //   ticker: 'USDT',
      //   price: 1,
      //   balance: 20000
      // },
      // {
      //   name: 'Ripple',
      //   ticker: 'XRP',
      //   price: 0.50,
      //   balance: 104844
      // },
      // {
      //   name: 'Polkadot',
      //   ticker: 'DOT',
      //   price: 15.95,
      //   balance: 904
      // },
      // {
      //   name: 'Cardano',
      //   ticker: 'ADA',
      //   price: 0.42,
      //   balance: 39456
      // },
      // {
      //   name: 'Chainlink',
      //   ticker: 'LINK',
      //   price: 22.38,
      //   balance: 500
      // },
      // {
      //   name: 'Uniswap',
      //   ticker: 'UNI',
      //   price: 19.56,
      //   balance: 475
      // }
    ]
  }

  componentDidMount = () => {
    axios.get('https://api.coinpaprika.com/v1/coins')
      .then( response => {
        let coinData = response.data.slice(0, COIN_COUNT).map( function(coin) {
          return {
            key: coin.id,
            name: coin.name,
            ticker: coin.symbol,
            balance: 0,
            price: 0
          };
        });
        console.log('setting state');
        this.setState({ coinData });
        console.log('Done setting state');
      })
      console.log('CDM is DONE')
  }


  handleBalanceVisibilityChange = () => {
    this.setState( function(oldState) {
      return {
        ...oldState,
        showBalance: !oldState.showBalance
      }

    })
  }

  handleRefresh = (valueChangeTicker) => {
    // make a const newCoinData and map it with the old coinDate (destructured)
    const newCoinData = this.state.coinData.map( function(values) {
      // declare a new price variable and bind to original price amount
      let newValues = { ...values };
      // control flow: if the valueChangeTicker matches the ticker in coinData, 
      // calculate new price and then bind that price to newPrice
      if(valueChangeTicker === values.ticker) {
        let randomPercentage = 0.995 + Math.random() * 0.01
        newValues.price = Math.floor((newValues.price * randomPercentage)*1000)/1000
      }
      // return that object to the newCoinData array for that iteration
      return newValues
    });
    // after we finish the mapping, swap the newCoinData for the original coinData in state
    this.setState({coinData: newCoinData})
  }

  render() {
    return (
      <Div>
        <ExchangeHeader />
        <AccountBalance 
          ticker={this.state.ticker} 
          amount={this.state.balance} 
          showBalance={this.state.showBalance} 
          handleBalanceVisibilityChange={this.handleBalanceVisibilityChange} />
        <CoinList 
          coinData={this.state.coinData} 
          handleRefresh={this.handleRefresh} 
          showBalance={this.state.showBalance}/>
      </Div>
    );
  }

}

export default App;
