import React from 'react'
import AccountBalance from './components/AccountBalance'
import { v4 as uuidv4 } from 'uuid'
import CoinList from './components/CoinList';
import ExchangeHeader from './components/ExchangeHeader';
import styled from 'styled-components'

const Div = styled.div`
  text-align: center;
  background-color: rgb(20, 56, 97);
  color: #cccccc;
`;


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      balance: 10000,
      coinData: [
        {
          name: 'Bitcoin',
          ticker: 'BTC',
          price: 35000
        },
        {
          name: 'Ethereum',
          ticker: 'ETH',
          price: 1400
        },
        {
          name: 'Tether',
          ticker: 'USDT',
          price: 1
        },
        {
          name: 'Ripple',
          ticker: 'XRP',
          price: 0.50
        },
        {
          name: 'Polkadot',
          ticker: 'DOT',
          price: 15.95
        },
        {
          name: 'Cardano',
          ticker: 'ADA',
          price: 0.42
        },
        {
          name: 'Chainlink',
          ticker: 'LINK',
          price: 22.38
        },
        {
          name: 'Uniswap',
          ticker: 'UNI',
          price: 19.56
        }
      ]
    }
  }
  render() {
    return (
      <Div>
        <ExchangeHeader />
        <AccountBalance amount={this.state.balance} />
        <CoinList coinData={this.state.coinData} />
      </Div>
    );
  }

}

export default App;
