import React from 'react'
import logo from './logo.svg'
import candlestick from './candlestick.png'
import './App.css';
import Coin from './components/Coin/Coin'
import AccountBalance from './components/AccountBalance/AccountBalance'

class App extends React.Component {
  constructor() {
    super(props);
    this.state = {
      balance: 10000,
      coinData: [
        {
          name: 'Bitcoin',
          ticker: 'BTC',
          price: 35000
        },
        {
          name: 'Ethereum',
          ticker: 'BTC',
          price: 1400
        },
        {
          name: 'Tether',
          ticker: 'BTC',
          price: 1
        },
        {
          name: 'XRP',
          ticker: 'BTC',
          price: 0.50
        },
        {
          name: 'Polkadot',
          ticker: 'BTC',
          price: 15.95
        },
        {
          name: 'Cardano',
          ticker: 'BTC',
          price: 0.42
        },
        {
          name: 'Chainlink',
          ticker: 'BTC',
          price: 22.38
        }
      ]
    }
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
            <img src={candlestick} alt="React logo" className="App-logo" />
            <h1 className="App-title">
              Coin Exchange
            </h1>
        </header>
        <AccountBalance amount={this.state.balance} />
        <table className="coin-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Ticker</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          <>
            <Coin name={this.state.coinDate[0].name} ticker={this.state.coinData[0].ticker} price={this.state.coinData[0].price}/>
            <Coin name={this.state.coinDate[1].name} ticker={this.state.coinData[1].ticker} price={this.state.coinData[1].price} />
            <Coin name={this.state.coinDate[2].name} ticker={this.state.coinData[2].ticker} price={this.state.coinData[2].price} />
            <Coin name={this.state.coinDate[3].name} ticker={this.state.coinData[3].ticker} price={this.state.coinData[3].price} />
            <Coin name={this.state.coinDate[4].name} ticker={this.state.coinData[4].ticker} price={this.state.coinData[4].price} />
            <Coin name={this.state.coinDate[5].name} ticker={this.state.coinData[5].ticker} price={this.state.coinData[5].price} />
            <Coin name={this.state.coinDate[6].name} ticker={this.state.coinData[6].ticker} price={this.state.coinData[6].price} />

          </>
        </tbody>
      </table>
      </div>
    );
  }

}

export default App;
